process-model.temporary-variables:
Packet*		pkptr;
double		pk_size;
double		ete_delay;

process-model.function-block:
static void call_req_handle(void)
{
	Packet *pkptr;
	int burst_type = Opencell_Burst_Access;
	int signalling_type =Opencell_RR_Chan_Req; 

    FIN(call_req_handle());

    op_intrpt_schedule_remote (op_sim_time(), RR_Phy_Conn_Req, phy_objid);

	/*Create signalling packet*/
	/*Currently I'm not sure where the RIL3 ChannReq signalling is dumped. Dump it here temporarily*/
	pkptr = op_pk_create(Opencell_Burst_Overhead_Size_AB+Opencell_Burst_Payload_Size_AB);
	op_pk_fd_set(pkptr, Opencell_Burst_Fd_Index_Type, OPC_FIELD_TYPE_INTEGER, & burst_type , OPC_FIELD_SIZE_UNCHANGED);
	op_pk_fd_set(pkptr, Opencell_Burst_Fd_Index_Signalling, OPC_FIELD_TYPE_INTEGER, & signalling_type, OPC_FIELD_SIZE_UNCHANGED);

	op_pk_send (pkptr, phy_ostrm);

    FOUT;
}

attribute.value-text:
/*
stream    :  RR [0] -> phy [2]
stream    :  phy [1] -> RR [0]
*/

Objid node_objid;
node_objid = op_topo_parent(op_id_self());
phy_objid = op_id_frome_name (node_objid, OPC_OBJTYPE_PROC, "phy");
phy_ostrm = 0;
phy_istrm = 0;
